import React,{useEffect,useState} from 'react'
import styled from 'styled-components'
import Check from '../check.png'
import { useDispatch,useSelector } from 'react-redux'
import { actionCreators as dateActions } from '../redux/module/date'
import Monday from './Monday'
import PostModal from './PostModal'
import Tuesday from './Tuesday'
import Wednesday from './Wednesday'
import Thursday from './Thursday'
const Main = () => {
    const dispatch =useDispatch();
    const [modal,setModal] = useState("");
    const getModal = useSelector((state)=>state.date.modal)
    const monday = useSelector((state)=>state.date.monday)
    const tuesday = useSelector((state)=>state.date.tuesday)
    const wednesday = useSelector((state)=>state.date.wednesday)
    const thursday = useSelector((state)=>state.date.thursday)
    const friday = useSelector((state)=>state.date.friday)
    const saturday = useSelector((state)=>state.date.saturday)
    const sunday = useSelector((state)=>state.date.tuesday)
    console.log(monday)
    useEffect(()=>{
     dispatch(dateActions.getDayMD("Monday"))
     dispatch(dateActions.getDayMD("Tuesday"))
     dispatch(dateActions.getDayMD("Wednesday"))
     dispatch(dateActions.getDayMD("Thursday"))
     dispatch(dateActions.getDayMD("Friday"))
     dispatch(dateActions.getDayMD("Saturday"))
     dispatch(dateActions.getDayMD("Sunday"))
    },[monday.length,tuesday.length,wednesday.length,thursday.length,friday.length,saturday.length,sunday.length])
    return (

      <Wrap>
           {
            modal? <PostModal setModal={setModal}/> : ""
        }
      <Header>
      <Line/> Weekly Schedule <Line/>
      </Header>

          <TextArea>
              <TextField>
                <Date>
                  Monday
                </Date>
                <DateLine/>
               <Monday monday={monday}/>
              </TextField>
              <TextField>
                <Date>
                TuesDay
                </Date>
                <DateLine/>
                <Tuesday tuesday={tuesday}/>
              </TextField>
              <TextField>
                <Date>
                  Wednesday
                </Date>
                <DateLine/>
               <Wednesday wednesday={wednesday}/>
              </TextField>
              <TextField>
                <Date>
                Thursday
                </Date>
                <DateLine/>
               <Thursday thursday ={thursday} />
              </TextField>
              <TextField>
                <Date>
                 Friday
                </Date>
                <DateLine/>

              </TextField>
              <TextField>
                <Date>
                  Saturday
                </Date>
                <DateLine/>
                <CheckList>



                    <CheckBox>
                        <Box>
                        <img src={Check} width="20px" margin="20px" alt=""/>
                        </Box>

                    TS수업 1~3강
                    </CheckBox>
                    <CheckBox>
                        <Box>
                        <img src={Check} width="20px" margin="20px" alt=""/>
                        </Box>

                    TS수업 1~3강
                    </CheckBox>
                    <CheckBox>
                        <Box>
                        <img src={Check} width="20px" margin="20px" alt=""/>
                        </Box>

                    TS수업 1~3강
                    </CheckBox>

                </CheckList>
              </TextField>
              <TextField>
                <Date>
                  Sunday
                </Date>
                <DateLine/>
                <CheckList>



                    <CheckBox>
                        <Box>
                        <img src={Check} width="20px" margin="20px" alt=""/>
                        </Box>

                    TS수업 1~3강
                    </CheckBox>
                    <CheckBox>
                        <Box>
                        <img src={Check} width="20px" margin="20px" alt=""/>
                        </Box>

                    TS수업 1~3강
                    </CheckBox>
                    <CheckBox>
                        <Box>
                        <img src={Check} width="20px" margin="20px" alt=""/>
                        </Box>

                    TS수업 1~3강
                    </CheckBox>

                </CheckList>
              </TextField>
              <TextField2>
              <Date>
                  Notes
                </Date>
                <DateLine/>
                <CheckList>



                    <CheckBox>
                        <Box>
                        <img src={Check} width="20px" margin="20px" alt=""/>
                        </Box>

                    TS수업 1~3강
                    </CheckBox>
                    <CheckBox>
                        <Box>
                        <img src={Check} width="20px" margin="20px" alt=""/>
                        </Box>

                    TS수업 1~3강
                    </CheckBox>
                    <CheckBox>
                        <Box>
                        <img src={Check} width="20px" margin="20px" alt=""/>
                        </Box>

                    TS수업 1~3강
                    </CheckBox>

                </CheckList>
              </TextField2>
          </TextArea>

     <AddButton onClick={()=>setModal(true)}>+</AddButton>
      </Wrap>
    )
}
