import React, { useState } from "react";
import styled from "styled-components";


import { useDispatch } from "react-redux";

const PostModal = (props) => {
  const dispatch = useDispatch();
  const addMarker = () => {
    // dispatch(markerActions.addMarkerAX(marker));
  };

  return (
   <div>

    <Component onClick={props.close} />
      <ModalComponent>
        <ModalExitBtn onClick={props.close}>
          {/* <Close /> */}
        </ModalExitBtn>

        <ModalHeader>ADD SCHEDULE</ModalHeader>
        <ModalInput>
          출발지 


        </ModalInput>
        <ModalButtonContainer>
          <ModalSubmitBtn onClick={addMarker}> ADD </ModalSubmitBtn>
          <ModalCancelBtn onClick={addMarker}> CANCEL </ModalCancelBtn>
        </ModalButtonContainer>
      </ModalComponent>
      </div>
  );
};
const Component = styled.div`
  position: fixed;
  top: 0;
  left: 50%;
  transform: translateX(-50%);
  opacity: 0.4;
  height: 100vh;
  width: 100%;
  background-color: black;
  z-index: 10;
`;
const ModalComponent = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  width: 50%;
  height: 80%;
  transform: translate(-50%, -50%);
  background-color: white;
  z-index: 20;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  background: #F4D799;
  border: 4px solid #33322E;
  box-sizing: border-box;
  box-shadow: 12px 12px 0px #33322E;
border-radius: 24px;


`;
const ModalHeader = styled.div`
  margin-top: 30px;
  font-weight: 600;
  font-size: 18px;
`;
const ModalInput = styled.div`
  box-sizing: border-box;
  width: 50%;
`;
const ModalButtonContainer = styled.div`
  box-sizing: border-box;
  width: 50%;
  margin-bottom: 30px;
`;
const ModalSubmitBtn = styled.button`
  width: 30%;
  background-color: #F9F3E5;
  border: none;
  outline: none;
  padding: 10px 0;
  cursor: pointer;
  font-weight: 600;
  font-size: 15px;
  border-radius: 14px;
  margin-right:10px;
`;
const ModalCancelBtn = styled.button`
  width: 30%;
  background-color: red;
  border: none;
  outline: none;
  padding: 10px 0;
  cursor: pointer;
  font-weight: 600;
  font-size: 15px;
  border-radius: 14px;
  margin-left:10px;
`;
const ModalExitBtn = styled.button`
  position: absolute;
  top: 0;
  right: 0;
  padding: 8px 12px;
  cursor: pointer;
  background-color: transparent;
  outline: none;
  border: none;
  color: black;
`;

export default PostModal;
